section.programlisting:
{ 
 <domain> : 
 {
   <prop list> : 
   {
     "attr" : 
     {
       <attr name> : 
       { 
         "type" : <attribute type>,
         "desc" : <textual description of attribute>,
         "rw"   : true/false
       },
       ....
     }, 
     "op" :
     {
        <operation name> :
        {
          "args" : [
                     { 
                      "type" : <argument type>
                      "name" : <argument name>
                      "desc" : <textual description of argument>
                     },
                     .....
                    ],
          "ret"  : <return type>,
          "desc" : <textual description of operation>
        }, 
        .....
     },
     "not" : 
     {
        "name" : <name>,
        "desc" : <desc>,
        "types" : [ <type1>, <type2> ]
     }
   }, 
   ....
 },
 ....
}
